(function e(t,n,r){function s(o,u){if(!n[o]){if(!t[o]){var a=typeof require=="function"&&require;if(!u&&a)return a(o,!0);if(i)return i(o,!0);var f=new Error("Cannot find module '"+o+"'");throw f.code="MODULE_NOT_FOUND",f}var l=n[o]={exports:{}};t[o][0].call(l.exports,function(e){var n=t[o][1][e];return s(n?n:e)},l,l.exports,e,t,n,r)}return n[o].exports}var i=typeof require=="function"&&require;for(var o=0;o<r.length;o++)s(r[o]);return s})
/**
 * @jsx React.DOM
 */
'use strict';

var React = require('react'),
    Generator = require('./generator'),
    Compatibility = require('./components/compatibility.jsx');

var generator = new Generator();

function generateSeed() {
    generator.seed({
        width: 256,
        height: 256,
        borderProtection: 8,
        likelyhood: [
            0,
            4 / 1000,
            24 / 1000,
            640 / 1000,
            480 / 1000,
            480 / 1000,
            480 / 1000
        ],
        massRatio: 50,
        startingPoints: 60
    });
}

function generateHeightMap() {
    generator.createHeight({
        baseLevel: 0,
        flatten: 1,
        groundLevel: 3,
        randomize: 0.25,
        noiseOnWater: false
    });
}

function generateTextures() {
    generator.createBaseTextures({
        mountainGenerate: 6,
        seamless: false,
        terrain: 0,
        texture: 8,
        waterTexture: 5
    });
}

function getRandomizedPlayers() {
    return generator.getRandomPlayerPositions(7, 80);
}

function generateAndGetResources() {
    return generator.applyResources({});
}

var App = React.createClass({displayName: 'App',
    getInitialState: function() {
        return {
            compatibility: 'return-to-the-roots',
            players: [],
            resources: {},
            viewType: 'light'
        }
    },

    componentWillMount: function() {
        generator.setColorMap('alternative').then(function() {
            this.setState({
                viewType: 'pretty'
            });
            this.handleSeed();
        }.bind(this), this.handleSeed);
    },

    handleDraw: function() {
        console.time('Draw');
        generator.draw({
            canvas: this.refs.canvas.getDOMNode(),
            terrain: 0,
            viewType: 'pretty'
        });
        console.timeEnd('Draw');
    },

    handleSeed: function() {
        console.time('New Seed, Height Map, Textures and Resources');
        generateSeed();
        generateHeightMap();
        generateTextures();
        this.setState({
            resources: generateAndGetResources()
        });
        console.timeEnd('New Seed, Height Map, Textures and Resources');
        this.handleDraw();
    },

    handleHeight: function() {
        console.time('Height Map, Textures and Resources');
        generateHeightMap();
        generateTextures();
        this.setState({
            resources: generateAndGetResources()
        });
        console.timeEnd('Height Map, Textures and Resources');
        this.handleDraw();
    },

    handleTextures: function() {
        console.time('Textures and Resources');
        generateTextures();
        this.setState({
            resources: generateAndGetResources()
        });
        console.timeEnd('Textures and Resources');
        this.handleDraw();
    },

    handleResources: function() {
        console.time('Resources');
        this.setState({
            players: [],
            resources: generateAndGetResources()
        });
        console.timeEnd('Resources');
        this.handleDraw();
    },

    handlePlayers: function() {
        console.time('Players');
        this.setState({
            players: getRandomizedPlayers()
        });
        console.timeEnd('Players');
        this.handleDraw();
    },

    handleDownload: function() {
        saveAs(generator.getFileBlob({
            terrain: 0,
            title: 'Untitled'
        }), 'Untitled.swd');
    },

    handleCompatibility: function(value) {
        this.setState({
            compatibility: value
        });
    },

    render: function() {
        var gold = this.state.resources.mineGold || 0,
            coal = this.state.resources.mineCoal || 0,
            ironOre = this.state.resources.mineIronOre || 0,
            granite = this.state.resources.mineGranite || 0,
            mineTotal = gold + coal + ironOre + granite + 0.0001,
            players = this.state.players.map(function(player, index) {
                var className = 'player-position',
                    style = {
                        left: player.x + 'px',
                        top: player.y + 'px',
                    };

                if(index > 6) {
                    className += ' ' + className + '--rttr';
                }

                return (
                    React.DOM.i({className: className, id: 'player' + index, style: style})
                );
            });

        return React.DOM.div(null, 
            /*<Compatibility onChange={this.handleCompatibility} value={this.state.compatibility} />*/
            React.DOM.div({className: "player-positions"}, 
                React.DOM.canvas({width: "256", height: "256", ref: "canvas", className: "settlers2-map settlers2-map--greenland"}), 
                players
            ), 
            React.DOM.p(null, 
                React.DOM.button({onClick: this.handleSeed}, "New Seed"), 
                React.DOM.button({onClick: this.handleHeight}, "Create Height Map"), 
                React.DOM.button({onClick: this.handleTextures}, "Randomize Textures"), 
                React.DOM.button({onClick: this.handleResources}, "Randomize Resources"), 
                React.DOM.button({onClick: this.handlePlayers}, "Randomize Players"), 
                React.DOM.button({onClick: this.handleDownload}, "Download")
            ), 
            React.DOM.p(null, 
                React.DOM.dl({className: "generator-statistics"}, 
                    React.DOM.dt(null, "Players:"), 
                    React.DOM.dd(null, this.state.players.length), 
                    React.DOM.dt(null, "Trees:"), 
                    React.DOM.dd(null, this.state.resources.tree || 0), 
                    React.DOM.dt(null, "Granite:"), 
                    React.DOM.dd(null, this.state.resources.granite || 0), 
                    React.DOM.dt(null, "Coal:"), 
                    React.DOM.dd(null, Math.round(coal / mineTotal * 100, 1), " %"), 
                    React.DOM.dt(null, "Iron ore:"), 
                    React.DOM.dd(null, Math.round(ironOre / mineTotal * 100, 1), " %"), 
                    React.DOM.dt(null, "Gold:"), 
                    React.DOM.dd(null, Math.round(gold / mineTotal * 100, 1), " %"), 
                    React.DOM.dt(null, "Granite:"), 
                    React.DOM.dd(null, Math.round(granite / mineTotal * 100, 1), " %")
                )
            )
        )
    }
});

window.onload = function() {
    React.renderComponent(
        App(null),
        document.getElementById('app')
    );
}
'use strict';

var Promise = require('promise'),
    Map = require('./map');

var constants = require('./constants'),
    AREA = constants.AREA,
    CP437 = constants.CP437,
    COLOR = constants.COLOR,
    OBJECT_TYPE = constants.OBJECT_TYPE,
    RESOURCE = constants.RESOURCE,
    SITE = constants.SITE,
    TERRAIN = constants.TERRAIN,
    TEXTURE = constants.TEXTURE,
    TEXTURE_INFO = constants.TEXTURE_INFO,
    TREE_INFO = constants.TREE_INFO;

var Generator = function() {
    var map,
        x,
        y,
        i,
        j,
        k,
        l,
        around,
        aroundExpandTo,
        baseLevel,
        borderProtection,
        colorMap,
        colors = [],
        data,
        deletedNodes,
        height,
        index,
        mass,
        massRatio,
        nodes,
        players = [],
        size,
        seedMap,
        startingPoints,
        total,
        value,
        viewType,
        width;

    function expandTo(index, value, current) {
        aroundExpandTo = map.getNodesByIndex(index);

        seedMap[index] = value;
        if(current !== void 0) {
            delete nodes[current];
            deletedNodes.push(current);
        }
        mass++;

        Object.keys(aroundExpandTo).forEach(function(key) {
            index = aroundExpandTo[key];
            if(seedMap[index] === 0) {
                seedMap[index] = 1;
                if(deletedNodes.length) {
                    nodes[deletedNodes.pop()] = index
                } else {
                    nodes.push(index);
                }
            }
        });
    }

    var seed = function(options) {
        //if(!options || !options.length) options = {};
        var likelyhood = options.likelyhood,
            givenStartingPoints = ~~options.startingPoints,
            givenMassRatio = ~~options.massRatio

        //width = 1024 || (~~(Math.random() * 20) + 7) * 16,
        //height = 1024 || (~~(Math.random() * 20) + 7) * 16,
        width = ~~options.width;
        height = ~~options.height;
        size = width * height;
        borderProtection = ~~options.borderProtection;
        if(borderProtection) borderProtection = ~~(Math.min(width, height) / borderProtection);
        seedMap = new Uint8Array(size);

        // sanitize user input
        if(givenStartingPoints < 1) givenStartingPoints = 1;
        else if(givenStartingPoints > 512) givenStartingPoints = 512;
        if(givenStartingPoints > size >> 2) givenStartingPoints = size >> 2;
        
        if(givenMassRatio < 1) givenMassRatio = 1;
        else if(givenMassRatio > 99) givenMassRatio = 99;

        nodes = [];
        deletedNodes = [];
        mass = 0;
        massRatio = ~~(size / 100 * givenMassRatio);
        startingPoints = 0;

        map = new Map(width, height);
        data = map.getRawData();

        // randomize some starting points
        value = 255;
        while(startingPoints < givenStartingPoints) {
            x = ~~(Math.random() * (width - borderProtection * 2)) + borderProtection;
            y = ~~(Math.random() * (height - borderProtection * 2)) + borderProtection;
            index = y * width + x;

            if(seedMap[index] === 0) {
                expandTo(index, value);
                startingPoints++;
            }
        }

        // do the land expansion
        if(mass > 0) {
            while(mass < massRatio) {
                value--;
                for(i = nodes.length; i > 0; --i) {
                    index = nodes[i];
                    if(index !== void 0) {
                        total = 0;
                        around = map.getNodesByIndex(index);

                        if(seedMap[around.left] > 1) total++;
                        if(seedMap[around.right] > 1) total++;
                        if(seedMap[around.topLeft] > 1) total++;
                        if(seedMap[around.topRight] > 1) total++;
                        if(seedMap[around.bottomLeft] > 1) total++;
                        if(seedMap[around.bottomRight] > 1) total++;

                        if(Math.random() <= likelyhood[total]) expandTo(index, ~~(value / 7 * total) + 2, i);
                    }
                }
                if(value > 8) {}
                else if(value === 8 && Math.random() <= likelyhood[1]) value = 256;
                else if(value === 7 && Math.random() <= likelyhood[2]) value = 56;
                else if(value === 6 && Math.random() <= likelyhood[3]) value = 64;
                else if(value === 5 && Math.random() <= likelyhood[4]) value = 72;
                else if(value === 4 && Math.random() <= likelyhood[5]) value = 80;
                else if(value === 3 && Math.random() <= likelyhood[6]) value = 96;
                else /*if(value === 2) */value = 128;
            }
        }
    };

    //options: baseLevel
    var createHeight = function(options) {
        //if(!options || !options.length) options = {};
        baseLevel = options.baseLevel = ~~options.baseLevel;
        options.groundLevel = Math.abs(~~options.groundLevel);
        options.flatten = Math.abs(options.flatten);
        options.noiseOnWater = !!options.noiseOnWater;

        if(options.groundLevel > 5) options.groundLevel = 5;
        if(options.flatten < 1) options.flatten = 1;
        else if(options.flatten > 30) options.flatten = 30;

        map.initializeHeight(options.baseLevel);

        // push land up or down before we start!
        i = options.baseLevel <= 30 ? options.groundLevel : -options.groundLevel;
        index = 0;
        for(y = 0; y < height; y++) {
            for(x = 0; x < width; x++) {
                if(seedMap[index] > 1) {
                    map.changeHeight(x, y, 0, i);
                }
                index++;
            }
        }

        // draw the final height map based on what we have
        index = 0;
        for(y = 0; y < height; y++) {
            for(x = 0; x < width; x++) {
                value = seedMap[index];
                if(value > 1) {
                    around = map.getNodesByIndex(index);
                    // calculate avarage around node
                    i = Math.round((seedMap[around.left] +
                        seedMap[around.right] +
                        seedMap[around.topLeft] +
                        seedMap[around.topRight] +
                        seedMap[around.bottomLeft] +
                        seedMap[around.bottomRight]) / 6);
                    // go up or down
                    map.changeHeight(x, y, ((value & 15) & (i & 15)) / 2, ~~((value - i) / 8));
                }
                index++;
            }
        }

        // flatten
        if(options.flatten > 1) {
            baseLevel = ~~(baseLevel / options.flatten);
            for(i = 0; i < size; i++) {
                data[i] = ~~(data[i] / options.flatten);
            }
        }

        // some extra randomize
        if(options.randomize > 0) {
            if(!options.noiseOnWater) {
                index = 0;
                for(y = 0; y < height; y++) {
                    for(x = 0; x < width; x++) {
                        if(seedMap[index] > 1 || data[index] !== baseLevel) {
                            map.changeHeight(x, y, 0, ~~(Math.random() * ((options.randomize * 2) + 1) - options.randomize));
                        }
                        index++;
                    }
                }
            } else {
                index = 0;
                for(y = 0; y < height; y++) {
                    for(x = 0; x < width; x++) {
                        map.changeHeight(x, y, 0, ~~(Math.random() * ((options.randomize * 2) + 1) - options.randomize));
                        index++;
                    }
                }
            }
        }

        map.calculateLightMap();
    };

    var createBaseTextures = function(options) {
        var changed = false,
            i,
            j,
            heightTotal = new Uint32Array(60),
            smallestHeight = 60,
            biggestHeight = 0,
            mountainTextures = [1, 11, 12, 13],
            textureBlock1 = size * 1,
            textureBlock2 = size * 2,
            siteBlock = size * 8,
            siteNodes;

        // sanitize
        options.mountainGenerate = ~~options.mountainGenerate;
        options.seamless = !!~~options.seamless;
        options.texture = ~~options.texture & TEXTURE.TO_ID_VALUE;
        options.waterTexture = ~~options.waterTexture & TEXTURE.TO_ID_VALUE;

        map.initializeTexture(options.texture);

        // find out where we have the most mass
        for(i = 0; i < size; i++) {
            heightTotal[data[i]]++;
            if(smallestHeight > data[i]) smallestHeight = data[i];
            if(biggestHeight < data[i]) biggestHeight = data[i];
        }

        // draw water texture
        /*for(i = 0; i < size; i++) {
            j = seedMap[i] & 3;
            if(data[i] >= (baseLevel - 2) && data[i] <= (baseLevel + 2)) {
                if(seedMap[i] < 2) {
                    map.setTexture(i, options.waterTexture);
                } else if(j > 1) {
                    // Meadow / Pasture / Tundra
                    map.setTexture(i, 0x07 + (seedMap[i] & 3));
                } else if(j === 0) {
                    // Flower variant
                    map.setTexture(i, 0x0F);
                }
            } else if(j === 0 && smallestHeight >== data[i] - 5) {
                // Swamp
                map.setTexture(i, 0x03);
            }
        }*/

        map.calculateSiteMap();
        
        // draw mountain texture
        if(options.mountainGenerate === 7) {
            for(i = 0; i < size; i++) {
                if(!map.isAnyTextureWithAnyOfFlags(i, TEXTURE.ARID | TEXTURE.IMPASSABLE)) {
                    siteNodes = map.getNodesByIndex(i);
                    if(
                        (data[siteBlock + siteNodes.left] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.right] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.topLeft] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.topRight] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.bottomLeft] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.bottomRight] & 0xF7) === 0x01
                    ) {
                        map.setTexture(i, mountainTextures[~~(Math.random() * 4)]);
                    }
                }
            }
        } else if(options.mountainGenerate === 6) {
            for(i = 0; i < size; i++) {
                if((data[siteBlock + i] & 0xF7) === 0x01 && !map.isAnyTextureWithAnyOfFlags(i, TEXTURE.ARID | TEXTURE.IMPASSABLE)) {
                    siteNodes = map.getNodesByIndex(i);
                    if(
                        (data[siteBlock + siteNodes.left] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.right] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.topLeft] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.topRight] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.bottomLeft] & 0xF7) === 0x01
                        && (data[siteBlock + siteNodes.bottomRight] & 0xF7) === 0x01
                    ) {
                        map.setTexture(i, mountainTextures[~~(Math.random() * 4)]);
                    }
                }
            }
        } else {
            for(i = 0; i < size; i++) {
                if((data[siteBlock + i] & 0xF7) === 0x01 && !map.isAnyTextureWithAnyOfFlags(i, TEXTURE.ARID | TEXTURE.IMPASSABLE)) {
                    siteNodes = map.getNodesByIndex(i);
                    if(
                        options.mountainGenerate <= (((data[siteBlock + siteNodes.left] & 0xF7) === 0x01)
                        + ((data[siteBlock + siteNodes.right] & 0xF7) === 0x01)
                        + ((data[siteBlock + siteNodes.topLeft] & 0xF7) === 0x01)
                        + ((data[siteBlock + siteNodes.topRight] & 0xF7) === 0x01)
                        + ((data[siteBlock + siteNodes.bottomLeft] & 0xF7) === 0x01)
                        + ((data[siteBlock + siteNodes.bottomRight] & 0xF7) === 0x01))
                    ) {
                        map.setTexture(i, mountainTextures[~~(Math.random() * 4)]);
                    }
                }
            }
        }

        // seamless mode
        if(!options.seamless) {
            for(i = 0; i < width; i++) {
                switch(data[textureBlock1 + i]) {
                // savannah and steppe
                case 0x00:
                case 0x0E:
                    map.setTexture(i, 0x03); // swamp
                    break;
                // meadow
                case 0x08:
                case 0x09:
                case 0x0A:
                case 0x0F:
                    map.setTexture(i, 0x03); // swamp
                    break;
                // desert
                case 0x04:
                case 0x07:
                    map.setTexture(i, 0x02); // snow
                    break;
                // magenta
                case 0x11:
                    map.setTexture(i, 0x10); // lava
                    break;
                // mountain meadow
                case 0x12:
                case 0x22:
                    map.setTexture(i, 0x10); // lava
                    break;
                // mountain
                case 0x01:
                case 0x0B:
                case 0x0C:
                case 0x0D:
                    map.setTexture(i, 0x02); // snow
                    break;
                // water
                case 0x05:
                case 0x06:
                    map.setTexture(i, 0x13); // water (no ships)
                    break;
                default:
                    switch(data[textureBlock2 + i]) {
                    // savannah and steppe
                    case 0x00:
                    case 0x0E:
                        map.setTexture(i, 0x03); // swamp
                        break;
                    // meadow
                    case 0x08:
                    case 0x09:
                    case 0x0A:
                    case 0x0F:
                        map.setTexture(i, 0x03); // swamp
                        break;
                    // desert
                    case 0x04:
                    case 0x07:
                        map.setTexture(i, 0x02); // snow
                        break;
                    // magenta
                    case 0x11:
                        map.setTexture(i, 0x10); // lava
                        break;
                    // mountain meadow
                    case 0x12:
                    case 0x22:
                        map.setTexture(i, 0x10); // lava
                        break;
                    // mountain
                    case 0x01:
                    case 0x0B:
                    case 0x0C:
                    case 0x0D:
                        map.setTexture(i, 0x02); // snow
                        break;
                    // water
                    case 0x05:
                    case 0x06:
                        map.setTexture(i, 0x13); // water (no ships)
                        break;
                    }
                }
            }
            for(; i < size; i += width) {
                switch(data[textureBlock1 + i]) {
                // savannah and steppe
                case 0x00:
                case 0x0E:
                    map.setTexture(i, 0x03); // swamp
                    break;
                // meadow
                case 0x08:
                case 0x09:
                case 0x0A:
                case 0x0F:
                    map.setTexture(i, 0x03); // swamp
                    break;
                // desert
                case 0x04:
                case 0x07:
                    map.setTexture(i, 0x02); // snow
                    break;
                // magenta
                case 0x11:
                    map.setTexture(i, 0x10); // lava
                    break;
                // mountain meadow
                case 0x12:
                case 0x22:
                    map.setTexture(i, 0x10); // lava
                    break;
                // mountain
                case 0x01:
                case 0x0B:
                case 0x0C:
                case 0x0D:
                    map.setTexture(i, 0x02); // snow
                    break;
                // water
                case 0x05:
                case 0x06:
                    map.setTexture(i, 0x13); // water (no ships)
                    break;
                default:
                    switch(data[textureBlock2 + i]) {
                    // savannah and steppe
                    case 0x00:
                    case 0x0E:
                        map.setTexture(i, 0x03); // swamp
                        break;
                    // meadow
                    case 0x08:
                    case 0x09:
                    case 0x0A:
                    case 0x0F:
                        map.setTexture(i, 0x03); // swamp
                        break;
                    // desert
                    case 0x04:
                    case 0x07:
                        map.setTexture(i, 0x02); // snow
                        break;
                    // magenta
                    case 0x11:
                        map.setTexture(i, 0x10); // lava
                        break;
                    // mountain meadow
                    case 0x12:
                    case 0x22:
                        map.setTexture(i, 0x10); // lava
                        break;
                    // mountain
                    case 0x01:
                    case 0x0B:
                    case 0x0C:
                    case 0x0D:
                        map.setTexture(i, 0x02); // snow
                        break;
                    // water
                    case 0x05:
                    case 0x06:
                        map.setTexture(i, 0x13); // water (no ships)
                        break;
                    default:
                    }
                }
            }
        }

        map.calculateSiteMap();
    };

    var getRandomPlayerPositions = function(maxPlayerCount, radius) {
        players = [];

        // sanitize
        maxPlayerCount = ~~maxPlayerCount;
        if(maxPlayerCount < 0) maxPlayerCount = 0;
        else if(maxPlayerCount > 10) maxPlayerCount = 10;

        radius = ~~radius;

        function generateRandomPlayers(sites) {
            var index,
                nodes,
                x,
                y;

            if(sites.length > 0 && players.length < maxPlayerCount) {
                // randomize a position from given plausible sites
                index = sites[~~(Math.random() * sites.length)];
                x = index % width;
                y = ~~((index - x) / width);

                // getRadiusNodes returns a typed array; must convert it to regular array
                nodes = Array.apply([], map.getRadiusNodes(x, y, radius));

                // remove nodes near newly randomized player
                sites = sites.filter(function(index) {
                    return nodes.indexOf(index) === -1;
                });

                // add player to list of known players
                players.push({
                    index: index,
                    x: x,
                    y: y
                });

                // get the next player
                generateRandomPlayers(sites, radius);
            }
        }

        // start the recursive call (if necessary)
        if(maxPlayerCount > 0) generateRandomPlayers(map.getAllSitesOfType(SITE.CASTLE));

        return players;
    };

    var applyResources = function(options) {
        var i,
            j,
            k,
            eachTextureIsSameKind,
            usableLandmass = 0,
            newResource,
            nodes,
            resources = {
                freshWater: 0,
                mineCoal: 0,
                mineIronOre: 0,
                mineGold: 0,
                mineGranite: 0,
                fish: 0,
                granite: 0,
                tree: 0
            },
            texture,
            textureFlag,
            textures,
            textureBlocks = size,
            objectIndexBlock = size * 4,
            objectTypeBlock = size * 5,
            siteBlock = size * 8,
            touchBlock = size * 10,
            resourceBlock = size * 11;

        // clean up
        for(i = 0; i < size; i++) {
            data[objectIndexBlock + i] = 0;
            data[objectTypeBlock + i] = 0;
        }

        options = options || {};
        // sanitize values
        options.treeRatio = (options.treeRatio !== void 0) ? ~~options.treeRatio : 33;
